var myApp = new Framework7({
    modalTitle: 'Framework7',
    animateNavBackIcon: true,//接近ios
    pushState:true,//静态化路由
    swipeBackPage:true,//滑动返回，默认true
});

// Expose Internal DOM library
var $$ = Dom7;

// Add main view
var mainView = myApp.addView('.pages', {
    // Enable Dynamic Navbar for this view
    dynamicNavbar: true,
});
console.log($$(".pages"))
/* ===== Animation ===== */
// myApp.onPageInit('animation', function (page) {
    $$(".pages").find('.start').on('click', function () {
        $$('#animate-me')
            .animate(
                {
                    'width': 200,
                    'height': 50,
                    'margin-left': 50,
                    'margin-top': 0,
                },
                {
                    duration: 600,
                    easing: 'swing'
                }
            )
            .animate(
                {
                    'width': 50,
                    'height': 200,
                    'margin-left': 0,
                    'margin-top': 50,
                },
                {
                    duration: 600,
                    easing: 'swing'
                }
            )
            .animate(
                {
                    'width': 100,
                    'height': 100,
                    'margin-left': 0,
                    'margin-top': 0,
                },
                {
                    duration: 600,
                    easing: 'swing',
                    complete: function () {
                        myApp.alert('Animation completed');
                    }
                }
            );
    });